// Import tracery grammar as Node library.
var tracery = require('tracery-grammar')
const fs = require('fs');

// Read the grammar that was generated by Tracery++
const file = fs.readFileSync('translated_tracerypp_grammar.json', 'utf8');
var input_grammar =JSON.parse(file);

// Setup tracery grammarw
var tracery_grammar = tracery.createGrammar(input_grammar)
tracery_grammar.addModifiers(tracery.baseEngModifiers)
  
// Output text to the console
var times = 4
var add_margin = true

if (add_margin) console.log("\n")
for (i = 0; i < times; i ++) {
	var output = tracery_grammar.flatten('#origin#')
	console.log(output)
	if (add_margin) console.log("\n")
}
